package com.ex.beanlifecycle3;

import javax.annotation.PostConstruct;
import javax.annotation.PreDestroy;

public class Employee {

    private int id ;
    private  String name;

    public void setId(int id) {
        this.id = id;
    }

    public void setName(String name) {
        this.name = name;
    }

    @Override
    public String toString() {
        return "Employee{" +
                "id=" + id +
                ", name='" + name + '\'' +
                '}';
    }
    @PostConstruct
    public  void  init(){

        System.out.println("i am in init...."+id+" "+name);
    }
    @
    public  void  cleanUp(){
        System.out.println("i am now cleanUp...");

    }




}
